mapper.insert:
INSERT INTO CETUS_COLUMN_OPTIONS (
		   COLUMNS_UID
		 , LABEL
		 , NAME
		 , REG_UID
		 , REG_DT
		 , UPDT_UID
		 , UPDT_DT
) VALUES (
		   #{columnsUid}
		 , #{label}
		 , #{name}
		 , #{regUid}
		 , now()
		 , #{updtUid}
		 , now()
		 )

mapper.update:
UPDATE CETUS_COLUMN_OPTIONS
  SET LABEL         = #{label}
    , NAME          = #{name}
    , UPDT_UID      = #{updtUid}
    , UPDT_DT       = now()
WHERE UID = #{uid}
  AND COLUMNS_UID = #{columnsUid}
