mapper.select:
/* cetusFormColumns.list */ SELECT UID , WORKPLACE_UID , TYPE , LABEL , REQUIRED , DESCRIPTION , USE_AT , NAME , REG_UID , TO_CHAR(CU.REG_DT, 'YYYY-MM-DD HH24:MI') AS REG_DT , UPDT_UID , TO_CHAR(CU.UPDT_DT, 'YYYY-MM-DD HH24:MI') AS UPDT_DT , PLACEHOLDER , SORT_NUM , FORM_GROUP FROM CETUS_FORM_COLUMNS CU WHERE 1 = 1 AND WORKPLACE_UID = #{workplaceUid} AND FORM_GROUP = #{formGroup} AND USE_AT = 'Y'
AND CU.TYPE = #{type}
ORDER BY CU.SORT_NUM
OFFSET ${startNumber} LIMIT ${endNumber}

SELECT UID , WORKPLACE_UID , TYPE , LABEL , REQUIRED , DESCRIPTION , USE_AT , NAME , REG_UID , TO_CHAR(CU.REG_DT, 'YYYY-MM-DD HH24:MI') AS REG_DT , UPDT_UID , TO_CHAR(CU.UPDT_DT, 'YYYY-MM-DD HH24:MI') AS UPDT_DT , PLACEHOLDER , SORT_NUM , FORM_GROUP FROM CETUS_FORM_COLUMNS CU WHERE 1 = 1 AND WORKPLACE_UID = #{workplaceUid} AND FORM_GROUP = #{formGroup} AND USE_AT = 'Y'
AND CU.TYPE = #{type}
ORDER BY CU.SORT_NUM

/* cetusUser.userListCount */
SELECT COUNT(*)
FROM CETUS_FORM_COLUMNS CU
WHERE 1 = 1
AND WORKPLACE_UID = #{workplaceUid}
AND FORM_GROUP = #{formGroup}

	AND CU.TYPE = #{type}

