mapper.select:
SELECT COALESCE(MAX(SORT_NUM), 0) + 1
FROM CETUS_FORM_COLUMNS s
WHERE FORM_GROUP = #{formGroup}

mapper.update:
UPDATE CETUS_FORM_COLUMNS
  SET
      LABEL         = #{label}
    , REQUIRED      = #{required}
    , DESCRIPTION   = #{description}
    , USE_AT        = #{useAt}
    , NAME          = #{name}
    , UPDT_UID      = #{updtUid}
    , UPDT_DT       = now()
    , PLACEHOLDER   = #{placeholder}
    , SORT_NUM = #{sortNum}

    , DEFAULT_VALUE = ${defaultValue}

WHERE uid = #{uid}
