mapper.select:
/* cetusProgrmInfo.menuProgramPageList */ SELECT CPI.UID ,CPI.PROGRM_NM ,CPI.URL ,CPI.PROGRM_DC ,CPI.USE_AT ,CPI.REG_UID ,TO_CHAR(CPI.REG_DT, 'YYYY-MM-DD') AS REG_DT ,CU_REG.USER_NM AS REG_NM ,CPI.WORKPLACE_UID FROM CETUS_PROGRM_INFO CPI LEFT JOIN CETUS_USER CU_REG ON CU_REG.UID = CPI.REG_UID WHERE 1 = 1 AND CPI.WORKPLACE_UID = #{workplaceUid}::bigint
AND CPI.USE_AT = #{useAt}
AND (CPI.PROGRM_NM LIKE '%' || #{browseText} || '%' OR CPI.URL LIKE '%' || #{browseText} || '%' )
ORDER BY CPI.REG_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}

/* cetusProgrmInfo.menuProgramPageCount */ SELECT COUNT(*) FROM CETUS_PROGRM_INFO CPI LEFT JOIN CETUS_USER CU_REG ON CU_REG.UID = CPI.REG_UID WHERE 1 = 1 AND CPI.WORKPLACE_UID = #{workplaceUid}::bigint
AND CPI.USE_AT = #{useAt}
AND (CPI.PROGRM_NM LIKE '%' || #{browseText} || '%' OR CPI.URL LIKE '%' || #{browseText} || '%' )

/* cetusProgrmInfo.programPageList */ SELECT CPI.UID ,CPI.PROGRM_NM ,CPI.URL ,CPI.PROGRM_DC ,CPI.USE_AT ,CPI.REG_UID ,TO_CHAR(CPI.REG_DT, 'YYYY-MM-DD') AS REG_DT ,CU_REG.USER_NM AS REG_NM FROM CETUS_PROGRM_INFO CPI LEFT JOIN CETUS_USER CU_REG ON CU_REG.UID = CPI.REG_UID LEFT JOIN CETUS_WORKPLACE CW ON CW.UID = CPI.WORKPLACE_UID WHERE 1 = 1 AND CPI.WORKPLACE_UID = #{workplaceUid}::bigint
AND CPI.USE_AT = #{useAt}
AND (CPI.PROGRM_NM LIKE '%' || #{browseText} || '%' OR CPI.URL LIKE '%' || #{browseText} || '%' OR CU_REG.USER_NM LIKE '%' || #{browseText} || '%' )
ORDER BY CPI.REG_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}

/* cetusProgrmInfo.programPageCount */ SELECT COUNT(*) FROM CETUS_PROGRM_INFO CPI LEFT JOIN CETUS_USER CU_REG ON CU_REG.UID = CPI.REG_UID WHERE 1 = 1 AND CPI.WORKPLACE_UID = #{workplaceUid}::bigint
AND CPI.USE_AT = #{useAt}
AND (CPI.PROGRM_NM LIKE '%' || #{browseText} || '%' OR CPI.URL LIKE '%' || #{browseText} || '%' OR CU_REG.USER_NM LIKE '%' || #{browseText} || '%' )

mapper.insert:
/* cetusProgrmInfo.insert */
INSERT INTO CETUS_PROGRM_INFO (
     PROGRM_NM
    ,URL
    ,PROGRM_DC
    ,USE_AT
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
    ,WORKPLACE_UID
) VALUES (
     #{progrmNm}
    ,#{url}
    ,#{progrmDc}
    ,#{useAt}
    ,#{regUid}
    ,NOW()
    ,#{updtUid}
    ,NOW()
    ,#{workplaceUid}
)

mapper.update:
/* cetusProgrmInfo.update */
UPDATE CETUS_PROGRM_INFO
   SET PROGRM_NM = #{progrmNm}
      ,URL = #{url}
      ,PROGRM_DC = #{progrmDc}
      ,USE_AT = #{useAt}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}
