mapper.insert:
/* cetusUser.insert */
INSERT INTO CETUS_USER (
	USER_ID
   ,PASSWORD
   ,USER_NM
   ,APPROVE_AT
   ,USE_AT
   ,REG_DT
   ,UPDT_DT
   ,REG_UID
   ,UPDT_UID
) VALUES (
	#{userId}
   ,#{password}
   ,#{userNm}
   ,'Y'
   ,'Y'
   ,NOW()
   ,NOW()
   ,#{regUid}
   ,#{updtUid}
)

mapper.update:
	/* cetusUser.update */
	UPDATE CETUS_USER
	   SET UPDT_UID = #{updtUid}
	      ,UPDT_DT = NOW()
		  ,PASSWORD = #{password}
		  ,USER_NM = #{userNm}
     WHERE UID = #{uid}

/* cetusUser.updateFailCnt */
UPDATE CETUS_USER
   SET FAIL_CNT = COALESCE(FAIL_CNT, 0) + 1
 WHERE USER_ID = #{userId}

/* cetusUser.updateUserFailCntAndChangeLastLoginDt */
UPDATE CETUS_USER
   SET FAIL_CNT = 0
 WHERE USER_ID = #{userId}

/* cetusUser.updateUserUseAt */
UPDATE CETUS_USER
   SET USE_AT = (CASE USE_AT WHEN 'N' THEN 'Y' ELSE 'N' END)
      ,FAIL_CNT = FAIL_CNT + 1
 WHERE USER_ID = #{userId}

mapper.select:
/* cetusUser.excelPageList */ SELECT USER_ID ,USER_NM ,APPROVE_AT ,USE_AT ,'사용자 권한' AS ROLE_NM FROM CETUS_USER WHERE 1 = 1
AND APPROVE_AT = #{approveAt}
AND (USER_NM LIKE '%' || #{browseText} || '%' OR USER_ID LIKE '%' || #{browseText} || '%')
ORDER BY REG_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}

/* cetusUser.excelPageCount */
SELECT COUNT(*)
  FROM CETUS_USER
 WHERE 1 = 1

   AND APPROVE_AT = #{approveAt}


   AND (USER_NM LIKE '%' || #{browseText} || '%' OR USER_ID LIKE '%' || #{browseText} || '%')

/* cetusUser.userList */ SELECT CU.UID ,CU.USER_ID ,CU.USER_NM ,CU.APPROVE_AT ,CU.USE_AT ,TO_CHAR(CU.REG_DT, 'YYYY-MM-DD') AS REG_DT FROM CETUS_USER CU WHERE 1 = 1 ORDER BY CU.REG_DT DESC
AND CU.USER_ID LIKE '%' || #{userId} || '%'
OFFSET ${startNumber} LIMIT ${endNumber}
