mapper.delete:
/* cetusMobigenDatasetTag.deleteAll */
DELETE FROM CETUS_MOBIGEN_DATASET_TAG
      WHERE METADATA_ID = #{metadataId}

mapper.insert:
/* cetusMobigenDatasetTag.insert */
INSERT INTO CETUS_MOBIGEN_DATASET_TAG (
     METADATA_ID
    ,TAG_UID
) VALUES (
     #{metadataId}
    ,#{tagUid}
)

mapper.select:
/* cetusMobigenDatasetTag.getMobigenDatasetTagListByMetadataId */
SELECT CMDT.TAG_UID
      ,CMT.TAG_NM AS TAG_NM
  FROM CETUS_MOBIGEN_DATASET_TAG CMDT
 INNER JOIN CETUS_MOBIGEN_TAG CMT ON CMT.UID = CMDT.TAG_UID
 WHERE CMDT.METADATA_ID = #{metadataId}
