mapper.insert:
/* cetusPosition.insert */
INSERT INTO CETUS_POSITION (
     WORKPLACE_UID
    ,NAME
    ,SORT_ORDER
    ,USE_AT
    ,REG_DT
    ,REG_UID
    ,UPDT_DT
    ,UPDT_UID
) VALUES (
     #{workplaceUid}
    ,#{name}
    ,#{sortOrder}
    ,'Y'
    ,NOW()
    ,#{regUid}
    ,NOW()
    ,#{updtUid}
)

mapper.update:
/* cetusPosition.update */
UPDATE CETUS_POSITION
   SET NAME = #{name}
      ,SORT_ORDER = #{sortOrder}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}

/* cetusPosition.updateUseAtToN */
UPDATE CETUS_POSITION
   SET USE_AT = 'N'
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}
