mapper.select:
/* cetusDataset.getDatasetPage */ SELECT CAD.UID AS APPROVED_UID ,CAD.DATASET_ID ,TO_CHAR(CAD.APPROVED_DT, 'YYYY-MM-DD HH:MM') AS APPROVED_DT ,COALESCE(CU_CREATE.USER_NM, '자동 등록') AS CREATED_NM ,CU_APP.USER_NM AS APPROVED_NM ,( SELECT COALESCE(ROUND(SUM(CDM.RATINGS) / COUNT(*)), 0) FROM CETUS_DATASET_COMMENT CDM WHERE CDM.APPROVED_DATASET_UID = CAD.UID ) AS RATINGS FROM CETUS_APPROVED_DATASET CAD LEFT JOIN CETUS_USER CU_CREATE ON CU_CREATE.UID = CAD.CREATED_UID LEFT JOIN CETUS_USER CU_APP ON CU_APP.UID = CAD.APPROVED_UID WHERE CAD.WORKPLACE_UID = #{workplaceUid} AND SHOW_AT = 'Y'
AND CAD.APPROVED_DT BETWEEN #{startDate}::TIMESTAMP AND (#{endDate}::DATE + INTERVAL '1 day' - INTERVAL '1 second')
ORDER BY CAD.APPROVED_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}
