mapper.select:
/* cetusDatasetMainUi.getDatasetMainUiPage */ SELECT CDMU.UID ,CDMU.CODE ,CDMU.NAME ,CDMU.MAX_COUNT ,CDMU.DESC_LINE_CLAMP ,CDMU.TITLE_LINE_CLAMP ,CDMU.THUMB_USE_AT ,CDMU.WORKPLACE_UID ,CDMU.USE_AT ,CDMU.REG_UID ,CU.USER_NM AS REG_NM ,TO_CHAR(CDMU.REG_DT, 'YYYY-MM-DD') AS REG_DT FROM CETUS_DATASET_MAIN_UI CDMU LEFT JOIN CETUS_USER CU ON CU.UID = CDMU.REG_UID WHERE 1 = 1 AND CDMU.DELETE_AT = 'N' AND CDMU.WORKPLACE_UID = #{workplaceUid}
AND (CDMU.NAME LIKE '%' || #{browseText} || '%' OR CDMU.CODE LIKE '%' || #{browseText} || '%' OR CU.USER_NM LIKE '%' || #{browseText} || '%' )
ORDER BY CDMU.REG_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}

/* cetusDatasetMainUi.getCountByCode */
SELECT COUNT(*)
  FROM CETUS_DATASET_MAIN_UI CDMU
 WHERE DELETE_AT = 'N'
   AND CODE = #{code}
   AND WORKPLACE_UID = #{workplaceUid}

mapper.insert:
/* cetusDatasetMainUi.insert */
INSERT INTO CETUS_DATASET_MAIN_UI (
     CODE
    ,NAME

    ,MAX_COUNT


    ,DESC_LINE_CLAMP


    ,TITLE_LINE_CLAMP

    ,THUMB_USE_AT
    ,WORKPLACE_UID
    ,USE_AT
    ,DELETE_AT
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
) VALUES (
     #{code}
    ,#{name}

    ,#{maxCount}


    ,#{descLineClamp}


    ,#{titleLineClamp}

    ,#{thumbUseAt}
    ,#{workplaceUid}
    ,#{useAt}
    ,'N'
    ,#{regUid}
    ,NOW()
    ,#{updtUid}
    ,NOW()
)

mapper.select:
/* cetusDatasetMainUi.getCountDatasetMainUiUse */
SELECT COUNT(*)
  FROM CETUS_DATASET_UI CDU
 WHERE CDU.MAIN_UI_UID = #{uid}

mapper.update:
/* cetusDatasetMainUi.updateDatasetMainUidDeleteAt */
UPDATE CETUS_DATASET_MAIN_UI
   SET DELETE_AT = 'Y'
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}

/* cetusDatasetMainUi.update */
UPDATE CETUS_DATASET_MAIN_UI
   SET NAME = #{name}

      ,MAX_COUNT = #{maxCount}


      ,DESC_LINE_CLAMP #{descLineClamp}


      ,TITLE_LINE_CLAMP = #{titleLineClamp}

      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
      ,THUMB_USE_AT = #{thumbUseAt}
      ,USE_AT = #{useAt}
 WHERE UID = #{uid}
