mapper.select:
/* cetusApprovedDataset.getDatasetPage */ SELECT CAD.UID AS APPROVED_UID ,CAD.DATASET_ID ,TO_CHAR(CAD.APPROVED_DT, 'YYYY-MM-DD HH:MM') AS APPROVED_DT ,COALESCE(CU_CREATE.USER_NM, '자동 등록') AS CREATED_NM ,CU_APP.USER_NM AS APPROVER_NM ,( SELECT COALESCE(ROUND(SUM(CDM.RATINGS) / COUNT(*)), 0) FROM CETUS_DATASET_COMMENT CDM WHERE CDM.APPROVED_DATASET_UID = CAD.UID ) AS RATINGS ,CDUI.CATEGORY_UID AS CATEGORY_UID ,CDC.CATEGORY_NM AS CATEGORY_NM ,CDMU.UID AS MAIN_UI_UID ,CDMU.CODE AS MAIN_UI_CODE ,CDMU.TYPE_CD AS MAIN_UI_TYPE_CD ,CDUI.SHOW_AT AS SHOW_AT ,CDUI.EXTRA_JSON AS EXTRA_JSON FROM CETUS_APPROVED_DATASET CAD LEFT JOIN CETUS_DATASET_UI CDU ON CDU.APPROVED_DATASET_UID = CAD.UID LEFT JOIN CETUS_USER CU_CREATE ON CU_CREATE.USER_ID = CAD.CREATED_ID LEFT JOIN CETUS_USER CU_APP ON CU_APP.UID = CAD.APPROVER_UID LEFT JOIN CETUS_DATASET_UI CDUI ON CDUI.APPROVED_DATASET_UID = CAD.UID LEFT JOIN CETUS_DATASET_MAIN_UI CDMU ON CDMU.UID = CDUI.MAIN_UI_UID LEFT JOIN CETUS_DATASET_CATEGORY CDC ON CDC.UID = CDUI.CATEGORY_UID WHERE CAD.WORKPLACE_UID = #{workplaceUid} AND CAD.DELETE_AT = 'N'
AND CDUI.SHOW_AT = #{showAt}
AND CDMU.TYPE_CD = #{typeCd}
AND (CU_CREATE.USER_NM LIKE '%' || #{browseText} || '%' OR CDMU.CODE LIKE '%' || #{browseText} || '%' )
ORDER BY CAD.APPROVED_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}

/* cetusApprovedDataset.getDatasetPageCount */
SELECT COUNT(*)
  FROM CETUS_APPROVED_DATASET CAD
  LEFT JOIN CETUS_DATASET_UI CDU ON CDU.APPROVED_DATASET_UID = CAD.UID
  LEFT JOIN CETUS_USER CU_CREATE ON CU_CREATE.USER_ID = CAD.CREATED_ID
  LEFT JOIN CETUS_USER CU_APP ON CU_APP.UID = CAD.APPROVER_UID
  LEFT JOIN CETUS_DATASET_UI CDUI ON CDUI.APPROVED_DATASET_UID = CAD.UID
  LEFT JOIN CETUS_DATASET_MAIN_UI CDMU ON CDMU.UID = CDUI.MAIN_UI_UID
 WHERE CAD.WORKPLACE_UID = #{workplaceUid}
   AND CAD.DELETE_AT = 'N'

   AND CDUI.SHOW_AT = #{showAt}


   AND CDMU.TYPE_CD = #{typeCd}


   AND (CU_CREATE.USER_NM LIKE '%' || #{browseText} || '%' OR CDMU.CODE LIKE '%' || #{browseText} || '%' )

/* cetusApprovedDataset.getDatasetList */ SELECT CAD.UID AS APPROVED_UID ,CAD.DATASET_ID ,TO_CHAR(CAD.APPROVED_DT, 'YYYY-MM-DD HH:MM') AS APPROVED_DT ,COALESCE(CU_CREATE.USER_NM, '자동 등록') AS CREATED_NM ,CU_APP.USER_NM AS APPROVER_NM ,( SELECT COALESCE(ROUND(SUM(CDM.RATINGS) / COUNT(*)), 0) FROM CETUS_DATASET_COMMENT CDM WHERE CDM.APPROVED_DATASET_UID = CAD.UID ) AS RATINGS ,CF.FILE_ID AS THUMB_ID ,CASE WHEN EXISTS ( SELECT 1 FROM CETUS_DATASET_BOOKMARK B WHERE B.APPROVED_DATASET_UID = CAD.UID ) THEN 'Y' ELSE 'N' END AS BOOKMARK_YN ,CDUI.EXTRA_JSON AS EXTRA_JSON FROM CETUS_APPROVED_DATASET CAD LEFT JOIN CETUS_DATASET_UI CDU ON CDU.APPROVED_DATASET_UID = CAD.UID LEFT JOIN CETUS_USER CU_CREATE ON CU_CREATE.USER_ID = CAD.CREATED_ID LEFT JOIN CETUS_USER CU_APP ON CU_APP.UID = CAD.APPROVER_UID LEFT JOIN CETUS_DATASET_UI CDUI ON CDUI.APPROVED_DATASET_UID = CAD.UID LEFT JOIN CETUS_FILE CF ON CF.FILE_UID = CDUI.THUMB_UID AND CF.USE_AT = 'Y' AND CF.SAVED = 'Y' LEFT JOIN CETUS_DATASET_MAIN_UI CDMU ON CDMU.UID = CDUI.MAIN_UI_UID WHERE CAD.WORKPLACE_UID = #{workplaceUid} AND CAD.DELETE_AT = 'N' AND CDU.SHOW_AT = 'Y'
AND CAD.APPROVED_DT BETWEEN #{startDate}::TIMESTAMP AND (#{endDate}::DATE + INTERVAL '1 day' - INTERVAL '1 second')
ORDER BY CAD.APPROVED_DT DESC

/* cetusApprovedDataset.getApprovedDatasetIdList */
SELECT CAD.DATASET_ID
  FROM CETUS_APPROVED_DATASET CAD
 WHERE WORKPLACE_UID = #{workplaceUid}
   AND DELETE_AT = 'N'

mapper.insert:
