INSERT INTO CETUS_APPROVED_DATASET (
     DATASET_ID
    ,WORKPLACE_UID
    ,APPROVED_DT
    ,APPROVER_UID
    ,CREATED_ID
    ,DELETE_AT
) VALUES (
     #{datasetId}
    ,#{workplaceUid}
    ,NOW()
    ,#{approverUid}
    ,#{createdId}
    ,'N'
)

mapper.update:
/* cetusApprovedDataset.deleteApprovedDataset */
UPDATE CETUS_APPROVED_DATASET
   SET DELETE_AT = 'Y'
 WHERE UID = #{uid}
