mapper.update:
/* cetusDatasetFile.increaseDownCnt */
UPDATE CETUS_DATASET_FILE
   SET DOWN_CNT = DOWN_CNT + 1
 WHERE FILE_ID = #{fileId}
   AND FILE_UID = #{fileUid}

mapper.insert:
/* cetusDatasetFile.insert */
INSERT INTO CETUS_DATASET_FILE (
     FILE_ID
    ,ORG_FILE_NM
    ,FILE_PATH
    ,FILE_URL
    ,FILE_SIZE
    ,FILE_TYPE
    ,EXTENSION
    ,REG_DT
    ,UPDT_DT
    ,SAVED
    ,REG_ID
    ,METADATA_ID
    ,RAWDATA_ID
    ,DATA_TP_CD
) VALUES (
     #{fileId}
    ,#{orgFileNm}
    ,#{filePath}
    ,#{fileUrl}
    ,#{fileSize}
    ,#{fileType}
    ,#{extension}
    ,NOW()
    ,NOW()
    ,#{saved}
    ,#{regId}
    ,#{metadataId}
    ,#{rawdataId}
    ,#{dataTpCd}
)

mapper.delete:
/* cetusDatasetFile.deleteFile */
UPDATE CETUS_DATASET_FILE
   SET USE_AT = 'N'
      ,UPDT_DT = NOW()
 WHERE FILE_ID = #{fileId}
